import './App.css';
import React, { Component } from 'react';
import axios from 'axios';



class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      events: null,
      curTime : new Date().toLocaleString(),
      searchfield: ''
    }
  }


  // componentWillMount() {
  //   const url = `http://127.0.0.1:5000/`;
  //   axios.get(url).then(response => response.data)
  //   .then((data) => {
  //     this.setState({ news: data })
  //    })
  // }


  render () {
    var all_news = this.state.news
    if (!this.state.news) {
      return <span>Loading...</span>;
    }
    console.log(all_news[0]['picUrl'])
    return (
      <div className = "container">
        <p>实时信息</p>
        <p>数据时间 : {this.state.curTime}</p>
        {all_news.map(news => (
          <a className = "url" href = {news['url']}>
          <div className = "each_news">
            <img src={news['picUrl']} alt="Smiley face"/>
            <div className = "title">{news['title']} </div>
            <div className = "time">{news['publish_time']}</div>
            <div className = "media_name">{news['media_name']}</div>
          </div>
        </a>
        ))}
    </div>
    );
  }
}


export default App;
