import './App.css';
import React, { Component } from 'react';
import axios from 'axios';



class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      events: null,
      curTime : new Date().toLocaleString(),
      searchfield: ''
    }
  }


  componentWillMount() {
    const url = `http://127.0.0.1:5000/`;
    axios.get(url).then(response => response.data)
    .then((data) => {
      this.setState({ news: data })
     })
  }


  render () {
    var all_news = this.state.news
    if (!this.state.news) {
      return <span>Loading...</span>;
    }
    console.log(all_news[0]['picUrl'])
    return (
      <div className = "container">
        <img className = "top_logo" src = 'http://www.cimc.com/shtml/assets/images/logo.png' alt="Smiley face"/>
        <p className = "web_title">实时信息</p>
        <p className = "data_time">数据时间 : {this.state.curTime}</p>
        {all_news.map(news => (
          <a className = "url" href = {news['url']}>
          <div className = "each_news">
            <img className = "preview_pic" src = 'https://www.cimc.com/en/uploadfile/2016/0308/20160308114409462.jpg' alt="Smiley face"/>
            <div className = "title">{news['title']} </div>
            <div className = "time">{news['publish_time']}</div>
            <div className = "media_name">{news['media_name']}</div>
          </div>
        </a>
        ))}
    </div>
    );
  }
}


export default App;
