import React from 'react';
import './App.css';

function App() {

  class query_keyword extends React.Component{
    constructor(props) {
      super(props);
      this.state = {value: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

  }




  return (
    <div className="App">
        <form>
          <input type="text" name="keyword" placeholder="请输入搜索词" value={this.state.email} onChange={this.handleEmailChange} />
          <button type="button" onClick={this.handleLogin}>Login</button>
        </form>);
    </div>
  );
}

export default App;
