import './App.css';
import React, { Component } from 'react';
import axios from 'axios';
import { MdDashboard } from "react-icons/md";
import {MdSearch} from "react-icons/md";
import {MdKeyboardReturn} from "react-icons/md";


class Each_news extends Component {
    constructor(props) {
      super(props);
      this.state = {
        events: null,
        curTime : new Date().toLocaleString(),
        searchfield: '',
        value: '',
        news_url: "http://guba.sina.cn/view_515_235708.html"
      }
    }

    render () {
      return (
        <div className = "container_2">
          <img className = "top_logo_3" src = 'http://www.cimc.com/shtml/assets/images/logo.png' alt="Smiley face"/>
          <iframe className="preview_pdf" width="420px" height="720px" src= {this.state.news_url}/>
          <div className = "menu_bar">
              <span className = "dashboard"><MdDashboard size={38}/></span>
              <span className = "query_active"><MdSearch size={38}/></span>
              <span className = "return"><MdKeyboardReturn size={38}/></span>
              <br></br>
              <span className = "dashboard_word" >首页</span>
              <span className = "query_word">搜索</span>
              <span className = "return_word">返回</span>
          </div>
      </div>
      );


    }
  }


  export default Each_news;
