import './App.css';
import React, { Component } from 'react';
import axios from 'axios';



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      searchfield: ''
    }
  }

  componentDidMount() {
    axios.get('http://127.0.0.1:5000/')
    .then(res => res.json())
    .then( (response) => {
      this.setState({articles: response});
    })
    .catch(error => {
      console.log(error);
    });
  }


  render () {
    return (
      <div>
           <ul>
                {this.state.articles.map(person => <li>{person}</li>)}
          </ul>
      </div>
    )
  }
}


export default App;
