import './App.css';
import React, { Component } from 'react';
import axios from 'axios';



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      searchfield: ''
    }
  }

  componentDidMount() {
    axios.get('http://127.0.0.1:5000/')
    .then(response => {
      this.setState({articles: response});
      console.log(response[0])
    })
    .catch(error => {
      console.log(error);
    });
  }


  render () {
      return (
          articles
      );

  }
}


export default App;
