import './UI.css';
import React, { Component } from 'react';
import axios from 'axios';
import { MdDashboard } from "react-icons/md";
import {MdSearch} from "react-icons/md";
import {MdKeyboardReturn} from "react-icons/md";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Each_new from './Each_news';

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      events: null,
      curTime : new Date().toLocaleString(),
      searchfield: '',
      clicked: false
    }
  }

  componentWillMount() {
    const url = `http://127.0.0.1:5000/`;
    axios.get(url).then((response => response.data))
    .then((data) => {
      this.setState({ news: data })
     })
  }

  handleClick() {
    this.setState({
      clicked: true
    });
  }


  render () {
    var all_news = this.state.news
    if (!this.state.news) {
      return <span>Loading...</span>;
    }
    var total = all_news.length

    const ColoredLine = ({ color }) => (
      <hr
          style={{
              color: color,
              backgroundColor: color,
              width: '150px'
          }}
      />

  );

    return (
      <div className = "container">
        <img className = "top_logo" src = 'http://www.cimc.com/shtml/assets/images/logo.png' alt="Smiley face"/>
        <p className = "web_title">实时资讯</p>
        <div className = "line_1"><ColoredLine color="lightblue" /></div>
        <p className = "data_time">数据时间 : {this.state.curTime}</p>
        <div className = "line_2" ><ColoredLine color="lightblue" /></div>
        {all_news.map(news => (

        <button onClick={this.handleClick}>
        {this.state.clicked ? <NewComponent /> : null}
        {/* <Link class="nav-link" to={'/each_new'}> */}
          <div className = "each_news">
            <span><img className = "preview_pic" src = 'https://www.cimc.com/en/uploadfile/2016/0308/20160308114409462.jpg' alt="Smiley face"/></span>
            <span className = "title">{news['title']} </span>
            <span className = "media_name">{news['media_name']}</span>
            <span className = "time">{news['publish_time']}</span>
          </div>
        </button>
         /* </Link> */
        ))}
        <div className = "padding"></div>
        <div className = "menu_bar">
              <Link class="nav-link" to="/"><span className = "dashboard_active"><MdDashboard size={38}/></span></Link>
              <Link class="nav-link" to="/query"><span className = "query"><MdSearch size={38}/></span></Link>
              <span className = "return"><MdKeyboardReturn size={38}/></span>
              <br></br>
              <Link class="nav-link" to="/"><span className = "dashboard_word" >首页</span></Link>
              <Link class="nav-link" to="/query"><span className = "query_word">搜索</span></Link>
              <span className = "return_word">返回</span>
        </div>
    </div>
    );
  }
}

export default Home;
