import './App.css';
import React, { Component } from 'react';
import $ from 'jquery';

const tasksURI = 'http://localhost:5000/';
const ajax = function(uri, method, data) {
  var request = {
      url: uri,
      type: method,
      contentType: "application/json",
      accepts: "application/json",
      cache: false,
      crossDomain: true,
      dataType: 'json',
      data: JSON.stringify(data),
      error: function(jqXHR) {
          console.log("ajax error " + jqXHR.status);
      }
  };
  return $.ajax(request);
}

class App extends Component {

    constructor(props) {
      super(props);
      this.state = {keyword: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event) {
      this.setState({keyword: event.target.value});
    }

    handleSubmit(event) {
      alert('A name was submitted: ' + this.state.keyword);
      event.preventDefault();
    }



  render () {
    return (
      <form onSubmit={this.handleSubmit} action = "http://localhost:5000/" method = "get">
        <label>
          Essay:
          <textarea keyword={this.state.keyword} onChange={this.handleChange} />
        </label>
        <input type="submit" keyword="Submit" />
      </form>
    );
  }
}


export default App;
