import './App.css';
import React, { Component } from 'react';
import $ from 'jquery';

const tasksURI = 'http://localhost:5000/todo/api/v1.0/tasks';

class App extends Component {

    constructor(props) {
      super(props);
      this.state = {keyword: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event) {
      this.setState({keyword: event.target.value});
    }

    handleSubmit(event) {
      alert('A name was submitted: ' + this.state.keyword);
      event.preventDefault();
    }



  render () {
    return (
      <form onSubmit={this.handleSubmit} action = "http://localhost:5000/" method = "get">
        <label>
          Essay:
          <textarea keyword={this.state.keyword} onChange={this.handleChange} />
        </label>
        <input type="submit" keyword="Submit" />
      </form>
    );
  }
}


export default App;
